// Calculate Perfect Inspection Criteria Score
const inspection = $input.item.json;

// Initialize scoring components
let photoScore = 0;
let deficiencyScore = 0;
let completenessScore = 0;
let qualityIssues = [];
let recommendations = [];

// Photo Analysis (30% of total score)
const photos = inspection.photos || [];
const overviewPhotos = photos.filter(p => p.type === 'overview').length;
const deficiencyPhotos = photos.filter(p => p.type === 'deficiency').length;

if (overviewPhotos >= 4) {
  photoScore += 70; // Base score for minimum coverage
  if (overviewPhotos >= 6) photoScore += 15; // Bonus for comprehensive coverage
} else {
  qualityIssues.push(`Only ${overviewPhotos} overview photos (minimum 4 required)`);
  recommendations.push('Add overview photos from all roof directions (North, South, East, West)');
}

if (deficiencyPhotos >= (inspection.deficiencies?.length || 0) * 2) {
  photoScore += 15; // Adequate deficiency documentation
} else {
  qualityIssues.push('Insufficient deficiency photos (recommend 2+ per deficiency)');
  recommendations.push('Capture multiple angles of each deficiency for better documentation');
}

// Deficiency Analysis (40% of total score)
const deficiencies = inspection.deficiencies || [];
if (deficiencies.length > 0) {
  deficiencyScore += 30; // Base score for having deficiencies

  let completeDeficiencies = 0;
  deficiencies.forEach(def => {
    let defComplete = true;

    // Check required fields
    if (!def.description || def.description.length < 20) {
      defComplete = false;
      qualityIssues.push(`Deficiency "${def.category || 'Unknown'}" needs detailed description`);
    }

    if (!def.location || def.location.trim() === '') {
      defComplete = false;
      qualityIssues.push(`Deficiency "${def.category || 'Unknown'}" missing location`);
    }

    if (!def.budgetAmount && !def.estimatedCost) {
      defComplete = false;
      qualityIssues.push(`Deficiency "${def.category || 'Unknown'}" missing cost estimate`);
    }

    if (defComplete) completeDeficiencies++;
  });

  deficiencyScore += (completeDeficiencies / deficiencies.length) * 30;
} else {
  qualityIssues.push('No deficiencies documented');
  recommendations.push('Ensure thorough inspection covers all potential issues');
}

// Completeness Analysis (30% of total score)
if (inspection.executiveSummary?.summaryText && inspection.executiveSummary.summaryText.length > 100) {
  completenessScore += 40;
} else {
  qualityIssues.push('Executive summary missing or too brief');
  recommendations.push('Provide comprehensive executive summary (minimum 100 characters)');
}

if (inspection.notes && inspection.notes.length > 50) {
  completenessScore += 20;
} else {
  qualityIssues.push('Inspection notes missing or too brief');
  recommendations.push('Add detailed inspection notes covering methodology and observations');
}

if (inspection.weather_conditions) {
  completenessScore += 10;
} else {
  qualityIssues.push('Weather conditions not documented');
  recommendations.push('Document weather conditions during inspection');
}

// Safety and Access Documentation
const hasAccessNotes = inspection.notes?.toLowerCase().includes('access') || 
                      inspection.notes?.toLowerCase().includes('safety') ||
                      inspection.executiveSummary?.inspectorNotes?.toLowerCase().includes('safety');

if (hasAccessNotes) {
  completenessScore += 10;
} else {
  qualityIssues.push('Safety and access procedures not documented');
  recommendations.push('Document roof access method and safety measures taken');
}

// Calculate final score
const totalScore = Math.round((photoScore * 0.3) + (deficiencyScore * 0.4) + (completenessScore * 0.3));

// Determine quality grade
let qualityGrade, qualityStatus;
if (totalScore >= 90) {
  qualityGrade = 'Excellent';
  qualityStatus = 'approved';
} else if (totalScore >= 80) {
  qualityGrade = 'Good';
  qualityStatus = 'approved_with_notes';
} else if (totalScore >= 70) {
  qualityGrade = 'Fair';
  qualityStatus = 'needs_improvement';
} else {
  qualityGrade = 'Poor';
  qualityStatus = 'requires_revision';
}

// Generate industry cost validation
const costValidation = deficiencies.map(def => {
  const cost = def.budgetAmount || def.estimatedCost || 0;
  let validation = { category: def.category, cost: cost, status: 'reasonable' };

  // Basic cost validation rules (in real implementation, use industry database)
  switch(def.category?.toLowerCase()) {
    case 'membrane failures':
      if (cost < 500) validation.status = 'too_low';
      if (cost > 15000) validation.status = 'too_high';
      break;
    case 'immediate repair':
      if (cost < 200) validation.status = 'too_low';
      break;
    case 'flashing':
    case 'perimeter flashing':
    case 'curb flashing':
      if (cost < 100) validation.status = 'too_low';
      if (cost > 5000) validation.status = 'too_high';
      break;
  }

  return validation;
});

return [{
  json: {
    ...inspection,
    qualityScore: totalScore,
    qualityGrade: qualityGrade,
    qualityStatus: qualityStatus,
    qualityIssues: qualityIssues,
    recommendations: recommendations,
    costValidation: costValidation,
    photoAnalysis: {
      total: photos.length,
      overview: overviewPhotos,
      deficiency: deficiencyPhotos,
      score: Math.round(photoScore)
    },
    deficiencyAnalysis: {
      total: deficiencies.length,
      complete: deficiencies.filter(d => d.description && d.location && (d.budgetAmount || d.estimatedCost)).length,
      score: Math.round(deficiencyScore)
    },
    completenessAnalysis: {
      hasExecutiveSummary: !!(inspection.executiveSummary?.summaryText),
      hasNotes: !!(inspection.notes),
      hasWeatherConditions: !!(inspection.weather_conditions),
      hasSafetyNotes: hasAccessNotes,
      score: Math.round(completenessScore)
    }
  }
}];

// Parse AI response and generate enhanced report
const inspection = $input.item.json;
const aiResponse = JSON.parse($('openrouter-ai-review').item.json.choices[0].message.content);

// Create enhanced inspection report
const enhancedReport = {
  // Original inspection data
  inspectionId: inspection.inspection_id,
  propertyName: inspection.property_name,
  propertyAddress: inspection.property_address,
  inspectorName: inspection.inspector_name,
  inspectionDate: new Date().toISOString().split('T')[0],

  // Quality assessment
  qualityScore: inspection.qualityScore,
  qualityGrade: inspection.qualityGrade,
  qualityStatus: inspection.qualityStatus,

  // AI enhancements
  aiReview: aiResponse,

  // Enhanced executive summary with AI corrections
  enhancedExecutiveSummary: {
    originalSummary: inspection.executiveSummary?.summaryText || '',
    overallCondition: inspection.executiveSummary?.overallCondition || 'Fair',
    overallRating: inspection.executiveSummary?.overallRating || 3,
    keyFindings: inspection.executiveSummary?.keyFindings || [],
    criticalIssues: inspection.executiveSummary?.criticalIssues || [],
    recommendedActions: inspection.executiveSummary?.recommendedActions || [],
    budgetRecommendation: inspection.executiveSummary?.budgetRecommendation || 'Minor Repairs',
    nextInspectionDate: inspection.executiveSummary?.nextInspectionDate || '',
    aiEnhancements: aiResponse.recommendedEdits || []
  },

  // Enhanced deficiency list
  enhancedDeficiencies: (inspection.deficiencies || []).map(def => ({
    ...def,
    costValidation: inspection.costValidation?.find(cv => cv.category === def.category) || { status: 'reasonable' },
    aiSuggestions: aiResponse.recommendedEdits?.filter(edit => edit.includes(def.category)) || []
  })),

  // Photo analysis
  photoAnalysis: inspection.photoAnalysis,

  // Completeness metrics
  completenessMetrics: {
    ...inspection.completenessAnalysis,
    missingElements: inspection.qualityIssues,
    recommendations: inspection.recommendations
  },

  // Report metadata
  reportMetadata: {
    generatedAt: new Date().toISOString(),
    generatedBy: 'RoofMind AI Review System',
    version: '2.0',
    reviewStatus: aiResponse.finalRecommendation,
    requiresHumanReview: aiResponse.finalRecommendation !== 'APPROVE'
  }
};

// Generate PDF-ready HTML content
const htmlReport = `
<!DOCTYPE html>
<html>
<head>
    <title>Enhanced Inspection Report - ${enhancedReport.propertyName}</title>
    <style>
        body { font-family: 'Arial', sans-serif; margin: 0; padding: 20px; color: #333; line-height: 1.6; }
        .header { background: linear-gradient(135deg, #2563eb, #1d4ed8); color: white; padding: 30px; text-align: center; margin: -20px -20px 30px -20px; }
        .company-logo { font-size: 24px; font-weight: bold; margin-bottom: 10px; }
        .report-title { font-size: 20px; margin-bottom: 5px; }
        .report-subtitle { font-size: 14px; opacity: 0.9; }
        .quality-banner { background: ${enhancedReport.qualityScore >= 80 ? '#10b981' : enhancedReport.qualityScore >= 70 ? '#f59e0b' : '#ef4444'}; color: white; padding: 15px; text-align: center; margin: 20px 0; border-radius: 8px; }
        .section { margin: 30px 0; }
        .section-title { font-size: 18px; font-weight: bold; color: #1f2937; border-bottom: 2px solid #e5e7eb; padding-bottom: 8px; margin-bottom: 15px; }
        .property-info { background: #f8fafc; padding: 20px; border-radius: 8px; display: grid; grid-template-columns: repeat(auto-fit, minmax(200px, 1fr)); gap: 15px; }
        .info-item { }
        .info-label { font-weight: bold; color: #4b5563; font-size: 12px; text-transform: uppercase; }
        .info-value { font-size: 16px; margin-top: 2px; }
        .deficiency-item { border: 1px solid #e5e7eb; border-radius: 8px; padding: 20px; margin: 15px 0; }
        .deficiency-header { display: flex; justify-content: between; align-items: center; margin-bottom: 10px; }
        .deficiency-title { font-weight: bold; font-size: 16px; }
        .cost-badge { background: #10b981; color: white; padding: 4px 12px; border-radius: 20px; font-size: 12px; }
        .ai-suggestion { background: #eff6ff; border-left: 4px solid #3b82f6; padding: 12px; margin: 10px 0; font-style: italic; }
        .recommendations { background: #fef3c7; border: 1px solid #fbbf24; padding: 15px; border-radius: 8px; }
        .footer { margin-top: 50px; padding: 20px; background: #f9fafb; text-align: center; color: #6b7280; font-size: 12px; }
        @media print { .header { background: #2563eb !important; } }
    </style>
</head>
<body>
    <div class="header">
        <div class="company-logo">üè† ROOFMIND</div>
        <div class="report-title">Enhanced Inspection Report</div>
        <div class="report-subtitle">AI-Reviewed & Quality Assured</div>
    </div>

    <div class="quality-banner">
        <strong>Quality Score: ${enhancedReport.qualityScore}/100 (${enhancedReport.qualityGrade})</strong>
        <br>Status: ${enhancedReport.aiReview.finalRecommendation}
    </div>

    <div class="section">
        <div class="section-title">Property Information</div>
        <div class="property-info">
            <div class="info-item">
                <div class="info-label">Property Name</div>
                <div class="info-value">${enhancedReport.propertyName}</div>
            </div>
            <div class="info-item">
                <div class="info-label">Address</div>
                <div class="info-value">${enhancedReport.propertyAddress || 'Not provided'}</div>
            </div>
            <div class="info-item">
                <div class="info-label">Inspector</div>
                <div class="info-value">${enhancedReport.inspectorName}</div>
            </div>
            <div class="info-item">
                <div class="info-label">Inspection Date</div>
                <div class="info-value">${enhancedReport.inspectionDate}</div>
            </div>
        </div>
    </div>

    <div class="section">
        <div class="section-title">Executive Summary</div>
        <p><strong>Overall Condition:</strong> ${enhancedReport.enhancedExecutiveSummary.overallCondition}</p>
        <p><strong>Rating:</strong> ${enhancedReport.enhancedExecutiveSummary.overallRating}/5 Stars</p>
        <p>${enhancedReport.enhancedExecutiveSummary.originalSummary}</p>

        ${enhancedReport.enhancedExecutiveSummary.aiEnhancements.length > 0 ? `
        <div class="ai-suggestion">
            <strong>AI Enhancement Suggestions:</strong>
            <ul>
                ${enhancedReport.enhancedExecutiveSummary.aiEnhancements.map(suggestion => `<li>${suggestion}</li>`).join('')}
            </ul>
        </div>` : ''}
    </div>

    <div class="section">
        <div class="section-title">Deficiencies Found (${enhancedReport.enhancedDeficiencies.length})</div>
        ${enhancedReport.enhancedDeficiencies.map(def => `
        <div class="deficiency-item">
            <div class="deficiency-header">
                <div class="deficiency-title">${def.category || 'Unknown Category'}</div>
                <div class="cost-badge">$${(def.budgetAmount || def.estimatedCost || 0).toLocaleString()}</div>
            </div>
            <p><strong>Location:</strong> ${def.location || 'Not specified'}</p>
            <p><strong>Description:</strong> ${def.description || 'No description provided'}</p>
            <p><strong>Severity:</strong> ${def.severity || 'Not specified'}</p>
            ${def.costValidation.status !== 'reasonable' ? `<p><strong>Cost Note:</strong> Estimate may be ${def.costValidation.status.replace('_', ' ')}</p>` : ''}
        </div>`).join('')}
    </div>

    <div class="section">
        <div class="section-title">Quality Analysis</div>
        <p><strong>Photo Coverage:</strong> ${enhancedReport.photoAnalysis.total} photos (${enhancedReport.photoAnalysis.overview} overview, ${enhancedReport.photoAnalysis.deficiency} deficiency)</p>
        <p><strong>Documentation Score:</strong> ${enhancedReport.completenessMetrics.score}/30</p>

        ${enhancedReport.completenessMetrics.missingElements.length > 0 ? `
        <div class="recommendations">
            <strong>Areas for Improvement:</strong>
            <ul>
                ${enhancedReport.completenessMetrics.missingElements.map(issue => `<li>${issue}</li>`).join('')}
            </ul>
        </div>` : ''}
    </div>

    <div class="section">
        <div class="section-title">AI Review Summary</div>
        <p><strong>Overall Assessment:</strong> ${enhancedReport.aiReview.overallAssessment}</p>
        <p><strong>Completeness Score:</strong> ${enhancedReport.aiReview.completenessScore}</p>
        <p><strong>Final Recommendation:</strong> ${enhancedReport.aiReview.finalRecommendation}</p>

        ${enhancedReport.aiReview.grammarCorrections?.length > 0 ? `
        <div class="ai-suggestion">
            <strong>Grammar & Spelling Notes:</strong>
            <ul>
                ${enhancedReport.aiReview.grammarCorrections.map(correction => `<li>${correction}</li>`).join('')}
            </ul>
        </div>` : ''}
    </div>

    <div class="footer">
        Report generated by RoofMind AI Review System v${enhancedReport.reportMetadata.version}<br>
        Generated on ${new Date(enhancedReport.reportMetadata.generatedAt).toLocaleDateString()}<br>
        Inspection ID: ${enhancedReport.inspectionId}
    </div>
</body>
</html>`;

return [{
  json: {
    enhancedReport: enhancedReport,
    htmlContent: htmlReport,
    pdfReady: true,
    needsHumanReview: enhancedReport.reportMetadata.requiresHumanReview
  }
}];
